import React from 'react';

const Hero = () => {
  return (
    <section className="relative bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 text-white overflow-hidden">
      {/* Enhanced Background Pattern */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute inset-0" style={{
          backgroundImage: 'radial-gradient(circle at 1px 1px, rgba(255,255,255,0.15) 1px, transparent 0)',
          backgroundSize: '30px 30px'
        }}></div>
      </div>
      
      {/* Floating Elements */}
      <div className="absolute top-20 left-10 w-20 h-20 bg-blue-400 rounded-full opacity-20 animate-pulse"></div>
      <div className="absolute top-40 right-20 w-16 h-16 bg-indigo-400 rounded-full opacity-30 animate-pulse" style={{animationDelay: '1s'}}></div>
      <div className="absolute bottom-40 left-20 w-12 h-12 bg-blue-300 rounded-full opacity-25 animate-pulse" style={{animationDelay: '2s'}}></div>
      
      <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-32 lg:py-40">
        <div className="text-center">
          <div className="space-y-8">
            {/* Badge */}
            <div className="inline-flex items-center px-4 py-2 bg-blue-800 bg-opacity-50 rounded-full text-blue-100 text-sm font-medium backdrop-blur-sm">
              <i className="fas fa-star mr-2"></i>
              Leading Digital Transformation in Africa
            </div>
            
            {/* Main Headline */}
            <h1 className="text-5xl md:text-7xl lg:text-8xl font-bold leading-tight">
              <span className="bg-gradient-to-r from-white to-blue-100 bg-clip-text text-transparent">
                StackShift
              </span>
              <br />
              <span className="text-blue-100">Consulting</span>
            </h1>
            
            {/* Tagline */}
            <p className="text-2xl md:text-3xl lg:text-4xl font-light text-blue-100 max-w-4xl mx-auto leading-relaxed">
              Digital Transformation for African Enterprises
            </p>
            
            {/* Description */}
            <p className="text-lg md:text-xl text-blue-200 max-w-3xl mx-auto leading-relaxed opacity-90">
              We help African businesses and institutions modernize their operations through strategic digital transformation, process automation, and AI adoption.
            </p>
            
            {/* CTA Buttons */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
              <a href="#contact" className="bg-white text-blue-900 px-8 py-4 rounded-xl font-semibold text-lg hover:bg-blue-50 transition-all duration-300 transform hover:scale-105 shadow-xl hover:shadow-2xl inline-block">
                Get a Quote
              </a>
              <a href="#services" className="border-2 border-white text-white px-8 py-4 rounded-xl font-semibold text-lg hover:bg-white hover:text-blue-900 transition-all duration-300 transform hover:scale-105 inline-block">
                Explore Services
              </a>
            </div>
          </div>
        </div>
      </div>
      
      {/* Enhanced Decorative elements */}
      <div className="absolute bottom-0 left-0 w-full h-40 bg-gradient-to-t from-gray-50 to-transparent"></div>
    </section>
  );
};

export default Hero;
